from typing import List
def suggestedProducts(products: List[str], searchWord: str) -> List[List[str]]:
    res = []
    firstLetter = searchWord[0]
    first = []
    for p in products:
        if p[0] == firstLetter:
            first.append(p)
    first.sort()
    res.append(first[:3])
    for i in range(1,len(searchWord)):
        temp = []
        letter = searchWord[i]
        for word in first:
            if len(word)<i+1:
                continue
            else:
                current = word[i]
                if current == letter:
                    temp.append(word)
        temp.sort()
        res.append(temp[:3])
        first = temp
    return res
                    
products = ["","","",""]
searchWord = ""
res = suggestedProducts(products,searchWord)